package com.example.food.desserts;

import android.os.Bundle;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import com.example.food.databinding.ActivityDessertsBinding;

import java.util.ArrayList;

public class DessertsActivity extends AppCompatActivity {

    private ActivityDessertsBinding binding;
    private ArrayList<Dessert> dessertList;
    private DessertAdapter adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        binding = ActivityDessertsBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());

        binding.recyclerView.setLayoutManager(new LinearLayoutManager(this));

        dessertList = new ArrayList<>();
        populateDessertList();

        adapter = new DessertAdapter(dessertList);
        binding.recyclerView.setAdapter(adapter);
    }

    private void populateDessertList() {
        dessertList.add(new Dessert("Тирамису", "data:image/jpeg;base64,", "Классический итальянский десерт"));
        dessertList.add(new Dessert("Чизкейк", "data:image/jpeg;base64,", "Сырный торт с кремом"));
        dessertList.add(new Dessert("Макарон", "data:image/jpeg;base64,", "Французские сладкие пирожные"));
        dessertList.add(new Dessert("Панна котта", "data:image/jpeg;base64,", "Итальянский десерт на основе сливок"));
        dessertList.add(new Dessert("Крем-брюле", "data:image/jpeg;base64,", "Крем с карамелизированной корочкой"));
        dessertList.add(new Dessert("Брауни", "data:image/jpeg;base64,", "Шоколадные пирожные с орехами"));
        dessertList.add(new Dessert("Сорбет", "data:image/jpeg;base64,", "Охлажденный фруктовый десерт"));
        dessertList.add(new Dessert("Кексы", "data:image/jpeg;base64,", "Мягкие кексы с различными наполнителями"));
        dessertList.add(new Dessert("Вафли", "data:image/jpeg;base64,", "Хрустящие вафли с фруктами"));
        dessertList.add(new Dessert("Ревани", "data:image/jpeg;base64,", "Турецкий десерт на основе манной крупы"));
    }
}
