package com.example.food.drinks;

import android.os.Bundle;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import com.example.food.databinding.ActivityDrinksBinding;

import java.util.ArrayList;

public class DrinksActivity extends AppCompatActivity {

    private ActivityDrinksBinding binding;
    private ArrayList<Drink> drinkList;
    private DrinkAdapter adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        binding = ActivityDrinksBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());

        binding.recyclerView.setLayoutManager(new LinearLayoutManager(this));

        drinkList = new ArrayList<>();
        populateDrinkList();

        adapter = new DrinkAdapter(drinkList);
        binding.recyclerView.setAdapter(adapter);
    }

    private void populateDrinkList() {
        drinkList.add(new Drink("Кола", "data:image/jpeg;base64,", "Газированный напиток"));
        drinkList.add(new Drink("Чай", "data:image/jpeg;base64,", "Горячий чай с лимоном"));
        drinkList.add(new Drink("Кофе", "data:image/jpeg;base64,", "Эспрессо с молоком"));
        drinkList.add(new Drink("Сок апельсиновый", "data:image/jpeg;base64,", "Свежевыжатый апельсиновый сок"));
        drinkList.add(new Drink("Вода", "data:image/jpeg;base64,", "Простая питьевая вода"));
        drinkList.add(new Drink("Лимонад", "data:image/jpeg;base64,", "Освежающий лимонад с мятой"));
        drinkList.add(new Drink("Молочный коктейль", "data:image/jpeg;base64,", "Коктейль с мороженым и молоком"));
        drinkList.add(new Drink("Энергетик", "data:image/jpeg;base64,", "Энергетический напиток"));
        drinkList.add(new Drink("Какао", "data:image/jpeg;base64,", "Какао с шоколадом"));
        drinkList.add(new Drink("Вино", "data:image/jpeg;base64,", "Красное вино из Франции"));
    }
}
